'use strict';
import _each from 'lodash/each';
import _clone from 'lodash/clone';
import _remove from 'lodash/remove';
import _assign from 'lodash/assign';
import Promise from "native-promise-only";
import { BaseComponent } from './base/Base';
export class FormioComponents extends BaseComponent {
  constructor(component, options, data) {
    super(component, options, data);
    this.type = 'components';
    this.components = [];
    this.hidden = [];
  }

  build() {
    this.createElement();
    this.addComponents();
  }

  getComponents() {
    return this.components;
  }

  /**
   * Perform a deep iteration over every component, including those
   * within other container based components.
   *
   * @param {function} cb - Called for every component.
   */
  everyComponent(cb) {
    let components = this.getComponents();
    _each(components, (component, index) => {
      if (component.type === 'components') {
        if (component.everyComponent(cb) === false) {
          return false;
        }
      }
      else if (cb(component, components, index) === false) {
        return false;
      }
    });
  }

  /**
   * Perform an iteration over each component within this container component.
   *
   * @param {function} cb - Called for each component
   */
  eachComponent(cb) {
    _each(this.getComponents(), (component, index) => {
      if (cb(component, index) === false) {
        return false;
      }
    });
  }

  /**
   * Returns a component provided a key. This performs a deep search within the
   * component tree.
   *
   * @param {string} key - The key of the component to retrieve.
   * @param {function} cb - Called with the component once found.
   * @return {Object} - The component that is located.
   */
  getComponent(key, cb) {
    let comp = null;
    this.everyComponent((component, components) => {
      if (component.component.key === key) {
        comp = component;
        if (cb) {
          cb(component, components);
        }
        return false;
      }
    });
    return comp;
  }

  /**
   * Return a component provided the Id of the component.
   *
   * @param {string} id - The Id of the component.
   * @param {function} cb - Called with the component once it is retrieved.
   * @return {Object} - The component retrieved.
   */
  getComponentById(id, cb) {
    let comp = null;
    this.everyComponent((component, components) => {
      if (component.id === id) {
        comp = component;
        if (cb) {
          cb(component, components);
        }
        return false;
      }
    });
    return comp;
  }

  /**
   * Add a new component to the components array.
   *
   * @param {Object} component - The component JSON schema to add.
   * @param {HTMLElement} element - The DOM element to append this child to.
   * @param {Object} data - The submission data object to house the data for this component.
   * @return {BaseComponent} - The created component instance.
   */
  addComponent(component, element, data) {
    element = element || this.element;
    data = data || this.data;
    let components = require('./index');
    component.row = this.row;
    let comp = components.create(component, this.options, data);
    this.components.push(comp);
    this.setHidden(comp);
    element.appendChild(comp.getElement());
    return comp;
  }

  /**
   * Remove a component from the components array.
   *
   * @param {BaseComponent} component - The component to remove from the components.
   * @param {Array<BaseComponent>} components - An array of components to remove this component from.
   */
  removeComponent(component, components) {
    component.destroy();
    let element = component.getElement();
    if (element && element.parentNode) {
      element.parentNode.removeChild(element);
    }
    _remove(components, {id: component.id});
  }

  /**
   * Removes a component provided the API key of that component.
   *
   * @param {string} key - The API key of the component to remove.
   * @param {function} cb - Called once the component is removed.
   * @return {null}
   */
  removeComponentByKey(key, cb) {
    let comp = this.getComponent(key, (component, components) => {
      this.removeComponent(component, components);
      if (cb) {
        cb(component, components);
      }
    });
    if (!comp) {
      if (cb) {
        cb(null);
      }
      return null;
    }
  }

  /**
   * Removes a component provided the Id of the component.
   *
   * @param {string} id - The Id of the component to remove.
   * @param {function} cb - Called when the component is removed.
   * @return {null}
   */
  removeComponentById(id, cb) {
    let comp = this.getComponentById(id, (component, components) => {
      this.removeComponent(component, components);
      if (cb) {
        cb(component, components);
      }
    });
    if (!comp) {
      if (cb) {
        cb(null);
      }
      return null;
    }
  }

  /**
   *
   * @param element
   * @param data
   */
  addComponents(element, data) {
    element = element || this.element;
    data = data || this.data;
    _each(this.component.components, (component) => this.addComponent(component, element, data));
  }

  updateValue(noValidate) {
    _each(this.components, (comp) => comp.updateValue(noValidate));
  }

  /**
   * A more performant way to check the conditions, calculations, and validity of
   * a submission once it has been changed.
   *
   * @param data
   * @param noValidate
   */
  checkData(data, noValidate) {
    _each(this.getComponents(), (comp) => {
      if (comp.type !== 'formcomponent') {
        comp.checkConditions(data);
        comp.calculateValue(data);
        if (!noValidate) {
          comp.checkValidity(data);
        }
      }
    });
  }

  checkConditions(data) {
    let show = super.checkConditions(data);
    _each(this.getComponents(), (comp) => {
      show |= comp.checkConditions(data);
    });
    return show;
  }

  /**
   * Allow components to hook into the next page trigger to perform their own logic.
   *
   * @return {*}
   */
  beforeNext() {
    var ops = [];
    _each(this.getComponents(), (comp) => ops.push(comp.beforeNext()));
    return Promise.all(ops);
  }

  /**
   * Allow components to hook into the submission to provide their own async data.
   *
   * @return {*}
   */
  beforeSubmit() {
    var ops = [];
    _each(this.getComponents(), (comp) => ops.push(comp.beforeSubmit()));
    return Promise.all(ops);
  }

  calculateValue(data) {
    super.calculateValue(data);
    _each(this.getComponents(), (comp) => comp.calculateValue(data));
  }

  checkValidity(data, dirty) {
    let check = super.checkValidity(data, dirty);
    _each(this.getComponents(), (comp) => {
      check &= comp.checkValidity(data, dirty);
    });
    return check;
  }

  destroy(all) {
    super.destroy(all);
    let components = _clone(this.components);
    _each(components, (comp) => this.removeComponent(comp, this.components));
  }

  set disabled(disabled) {
    _each(this.components, (component) => (component.disabled = disabled));
  }

  setHidden(component) {
    if (component.components && component.components.length) {
      component.hideComponents(this.hidden);
    }
    else if (component.component.hidden) {
      component.visible = false;
    }
    else {
      component.visible = (!this.hidden || (this.hidden.indexOf(component.component.key) === -1));
    }
  }

  hideComponents(hidden) {
    this.hidden = hidden;
    this.eachComponent((component) => this.setHidden(component));
  }

  get errors() {
    let errors = [];
    _each(this.getComponents(), (comp) => {
      let compErrors = comp.errors;
      if (compErrors.length) {
        errors = errors.concat(compErrors);
      }
    });
    return errors;
  }

  getValue() {
    return this.data;
  }

  setValue(value, noUpdate, noValidate) {
    if (!value) {
      return;
    }
    this.value = value;
    _each(this.getComponents(), (component) => {
      if (component.type === 'button') {
        return;
      }

      if (component.type === 'components') {
        component.setValue(value, noUpdate, noValidate);
      }
      else if (value && value.hasOwnProperty(component.component.key)) {
        component.setValue(value[component.component.key], noUpdate);
      }
      else if (component.component.input) {
        component.setValue(null, noUpdate, true);
      }
    });
  }
}

'use strict';
import { FormioComponents } from './Components';
import { Harness } from '../../test/harness';
import assert from 'power-assert';
import each from 'lodash/each';
describe('FormioComponents class', () => {
  let component = null;
  it('Should create a new FormioComponents class', (done) => {
    Harness.testCreate(FormioComponents, {
      components: [
        {
          type: 'textfield',
          key: 'firstName',
          input: true
        },
        {
          type: 'textfield',
          key: 'lastName',
          input: true
        }
      ]
    }).then((_component) => {
      component = _component;
      Harness.testElements(component, 'input[name="data[firstName]"]', 1);
      Harness.testElements(component, 'input[name="data[lastName]"]', 1);
      done();
    });
  });

  it('Should be able to add new components', () => {
    component.addComponent({
      type: 'email',
      key: 'email',
      input: true
    });
    Harness.testElements(component, 'input[name="data[firstName]"]', 1);
  });

  it('Should be able to set data within the components.', () => {
    let value = {
      firstName: 'Joe',
      lastName: 'Smith',
      email: 'joe@example.com'
    };
    component.setValue(value);
    assert.deepEqual(component.getValue(), value);
    each(component.components, (component) => {
      assert.equal(component.getValue(), value[component.component.key]);
    });
  });
});

import _get from 'lodash/get';
import _each from 'lodash/each';
import _has from 'lodash/has';
import _isArray from 'lodash/isArray';
import jsonLogic from 'json-logic-js';
export var Validator = {
  get: _get,
  each: _each,
  has: _has,
  boolValue: function(value) {
    if (typeof value === 'boolean') {
      return value;
    }
    else if (typeof value === 'string') {
      return (value.toLowerCase() === 'true');
    }
    else {
      return !!value;
    }
  },
  empty: function(value) {
    return value == null || value.length === 0;
  },
  name: function(component) {
    return component.label || component.placeholder || component.key;
  },
  checkValidator: function(validator, component, setting, value, data, row, t) {
    let result = validator.check.call(this, component, setting, value, data, row);
    if (typeof result === 'string') {
      return result;
    }
    if (!result) {
      return validator.message.call(this, component, setting, t);
    }
    return '';
  },
  validate: function(validator, component, value, data, row, t) {
    if (validator.key && _has(component, validator.key)) {
      let setting = this.get(component, validator.key);
      return this.checkValidator(validator, component, setting, value, data, row, t);
    }
    return this.checkValidator(validator, component, null, value, data, row, t);
  },
  check: function(validators, component, value, data, row, t) {
    let result = '';
    _each(validators, (name) => {
      if (this.validators.hasOwnProperty(name)) {
        let validator = this.validators[name];
        if (component.multiple && _isArray(value)) {
          _each(value, (val) => {
            result = this.validate(validator, component, val, data, row, t);
            if (result) {
              return false;
            }
          });
        }
        else {
          result = this.validate(validator, component, value, data, row, t);
        }
        if (result) {
          return false;
        }
      }
    });
    return result;
  },
  validators: {
    required: {
      key: 'validate.required',
      message: function(component, setting, t) {
        return t('required', {field: this.name(component)});
      },
      check: function(component, setting, value) {
        let required = Validator.boolValue(setting);
        if (!required) {
          return true;
        }
        return !Validator.empty(value);
      }
    },
    minLength: {
      key: 'validate.minLength',
      message: function(component, setting, t) {
        return t('minLength', {
          field: this.name(component),
          length: (setting - 1)
        });
      },
      check: function(component, setting, value) {
        let minLength = parseInt(setting, 10);
        if (!minLength || (typeof value !== 'string')) {
          return true;
        }
        return (value.length >= minLength);
      }
    },
    maxLength: {
      key: 'validate.maxLength',
      message: function(component, setting, t) {
        return t('maxLength', {
          field: this.name(component),
          length: (setting + 1)
        });
      },
      check: function(component, setting, value) {
        let maxLength = parseInt(setting, 10);
        if (!maxLength || (typeof value !== 'string')) {
          return true;
        }
        return (value.length <= maxLength);
      }
    },
    email: {
      message: function(component, setting, t) {
        return t('invalid_email', {
          field: this.name(component)
        });
      },
      check: function(component, setting, value) {
        // From http://stackoverflow.com/questions/46155/validate-email-address-in-javascript
        var re = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
        return re.test(value);
      }
    },
    date: {
      message: function(component, setting, t) {
        return t('invalid_date', {
          field: this.name(component)
        });
      },
      check: function(component, setting, value) {
        return (value !== 'Invalid date');
      }
    },
    pattern: {
      key: 'validate.pattern',
      message: function(component, setting, t) {
        return t('pattern', {
          field: this.name(component)
        });
      },
      check: function(component, setting, value) {
        let pattern = setting;
        if (!pattern) {
          return true;
        }
        let regexStr = '^' + pattern + '$';
        let regex = new RegExp(regexStr);
        return regex.test(value);
      }
    },
    json: {
      key: 'validate.json',
      check: function(component, setting, value, data, row) {
        if (!setting) {
          return true;
        }
        let valid = true;
        try {
          valid = jsonLogic.apply(setting, {
            data: data,
            row: row
          });
        }
        catch (err) {
          valid = err.message;
        }
        return valid;
      }
    },
    custom: {
      key: 'validate.custom',
      message: function(component, setting, t) {
        return t('custom', {
          field: this.name(component)
        });
      },
      check: function(component, setting, value, data, row) {
        if (!setting) {
          return true;
        }
        var valid = true;
        let custom = setting;
        /*eslint-disable no-unused-vars */
        var input = value;
        /*eslint-enable no-unused-vars */
        custom = custom.replace(/({{\s+(.*)\s+}})/, function (match, $1, $2) {
          if ($2.indexOf('data.') === 0) {
            return _get(data, $2.replace('data.', ''));
          }
          else if ($2.indexOf('row.') === 0) {
            return _get(row, $2.replace('row.', ''));
          }

          // Support legacy...
          return _get(data, $2);
        });

        /* jshint evil: true */
        eval(custom);
        return valid;
      }
    }
  }
};

'use strict';
import { Validator } from './Validator';
import assert from 'power-assert';
describe('Validator Tests', () => {
  it('Should test for minLength', () => {
    assert.equal(Validator.validators.minLength.check({}, 5, 'test'), false);
    assert.equal(Validator.validators.minLength.check({}, 4, 'test'), true);
    assert.equal(Validator.validators.minLength.check({}, 3, 'test'), true);
    assert.equal(Validator.validators.minLength.check({}, 6, 'test'), false);
    assert.equal(Validator.validators.minLength.check({}, 6, ''), false);
  });

  it('Should test for maxLength', () => {
    assert.equal(Validator.validators.maxLength.check({}, 5, 'test'), true);
    assert.equal(Validator.validators.maxLength.check({}, 4, 'test'), true);
    assert.equal(Validator.validators.maxLength.check({}, 3, 'test'), false);
    assert.equal(Validator.validators.maxLength.check({}, 6, 'test'), true);
    assert.equal(Validator.validators.maxLength.check({}, 6, ''), true);
  });

  it('Should test for email', () => {
    assert.equal(Validator.validators.email.check({}, '', 'test'), false);
    assert.equal(Validator.validators.email.check({}, '', 'test@a'), false);
    assert.equal(Validator.validators.email.check({}, '', 'test@example.com'), true);
    assert.equal(Validator.validators.email.check({}, '', 'test@a.com'), true);
    assert.equal(Validator.validators.email.check({}, '', 'test@a.co'), true);
  });

  it('Should test for required', () => {
    assert.equal(Validator.validators.required.check({}, true, ''), false);
    assert.equal(Validator.validators.required.check({}, true, 't'), true);
    assert.equal(Validator.validators.required.check({}, false, ''), true);
    assert.equal(Validator.validators.required.check({}, false, 'tes'), true);
    assert.equal(Validator.validators.required.check({}, true, undefined), false);
    assert.equal(Validator.validators.required.check({}, true, null), false);
    assert.equal(Validator.validators.required.check({}, true, []), false);
    assert.equal(Validator.validators.required.check({}, true, ['test']), true);
  });

  it('Should test for custom', () => {
    assert.equal(Validator.validators.custom.check({}, 'valid = (input == "test")', 'test'), true);
    assert.equal(Validator.validators.custom.check({}, 'valid = (input == "test")', 'test2'), false);
    assert.equal(Validator.validators.custom.check({}, 'valid = (input == "test") ? true : "Should be false."', 'test2'), 'Should be false.');
    assert.equal(Validator.validators.custom.check({}, 'valid = (input == "test") ? true : "Should be false."', 'test'), true);
  });

  it('Should test for pattern', () => {
    assert.equal(Validator.validators.pattern.check({}, 'A.*', 'A'), true);
    assert.equal(Validator.validators.pattern.check({}, 'A.*', 'Aaaa'), true);
    assert.equal(Validator.validators.pattern.check({}, '\w+', 'test'), false);
    assert.equal(Validator.validators.pattern.check({}, '\\w+', 'test'), true);
    assert.equal(Validator.validators.pattern.check({}, '\\w+@\\w+', 'test@a'), true);
    assert.equal(Validator.validators.pattern.check({}, '\\w+@\\w+', 'test@example.com'), false);
  });
});

import { TextFieldComponent } from '../textfield/TextField';
import { BaseComponent } from '../base/Base';
import _defaultsDeep from 'lodash/defaultsDeep';
import _delay from 'lodash/delay';

export class AddressComponent extends TextFieldComponent {
  constructor(component, options, data) {
    super(component, options, data);
    let src = 'https://maps.googleapis.com/maps/api/js?v=3&libraries=places&callback=googleMapsCallback';
    if (component.map && component.map.key) {
      src += '&key=' + component.map.key;
    }
    if (component.map && component.map.region) {
      src += '&region=' + component.map.region;
    }
    BaseComponent.requireLibrary('googleMaps', 'google.maps.places', src);
  }

  setValueAt(index, value) {
    this.value = value;
  }

  getValueAt(index) {
    return this.value;
  }

  /**
   * Start the autocomplete and the input listeners
   *
   * @param input
   *   The input field
   * @param autoCompleteOptions
   *   The default option for the autocompletion
   */
  autoCompleteInit(input, autoCompleteOptions) {
    // Set attribute autoComplete to off
    input.setAttribute("autocomplete", "off");

    // Init suggestions list
    this.autoCompleteSuggestions = []

    // Start Google AutocompleteService
    const autoComplete = new google.maps.places.AutocompleteService();

    // Create suggestions container
    const suggestionContainer = document.createElement('div');
    suggestionContainer.classList.add('pac-container', 'pac-logo');
    input.parentNode.appendChild(suggestionContainer);

    // Add listener on input field for input event
    this.addEventListener(input, 'input', (event) => {
      if (input.value) {
        let options = {
          input: input.value
        }
        autoComplete.getPlacePredictions(_defaultsDeep(options, autoCompleteOptions),
          (suggestions, status) => {
            this.autoCompleteDisplaySuggestions(suggestions, status, suggestionContainer, input);
        });
      } else {
        this.autoCompleteCleanSuggestions(suggestionContainer);
        suggestionContainer.style.display = 'none';
      }
    });
    // Add listener on input field for blur event
    this.addEventListener(input, 'blur', (event) => {
      // Delay to allow click on suggestion list
      _delay(function() {
        suggestionContainer.style.display = 'none';
      }, 100);
    });
    // Add listener on input field for focus event
    this.addEventListener(input, 'focus', (event) => {
      if (suggestionContainer.childElementCount) {
        suggestionContainer.style.display = 'block';
      }
    });
    // Add listener on input field for focus event
    this.addEventListener(window, 'resize', (event) => {
      // Set the same width as input field
      suggestionContainer.style.width = input.offsetWidth + 'px';
    });
    // Add listiner on input field for key event
    this.autoCompleteKeyboardListener(suggestionContainer, input);
  }

  /**
   * Add listiner on input field for key event
   *
   * @param suggestionContainer
   *   Suggestions container
   * @param input
   *   Input field to listen
   */
  autoCompleteKeyboardListener(suggestionContainer, input) {
    this.autoCompleteKeyCodeListener = (event) => {
      if (input.value) {
        switch (event.keyCode) {
          case 38:
          // UP
            this.autoCompleteKeyUpInteraction(suggestionContainer, input);
            break;

          case 40:
            // DOWN
            this.autoCompleteKeyDownInteraction(suggestionContainer, input);
            break;

          case 9:
            // TAB
            this.autoCompleteKeyValidationInteraction(suggestionContainer, input);
            break;

          case 13:
            // ENTER
            this.autoCompleteKeyValidationInteraction(suggestionContainer, input);
            break;
        }
      }
    };

    this.addEventListener(input, 'keydown', this.autoCompleteKeyCodeListener)
  }

  /**
   * Action when key up is trigger
   *
   * @param suggestionContainer
   *   Suggestions container
   * @param input
   *   Input field to listen
   */
  autoCompleteKeyUpInteraction(suggestionContainer, input) {
    let elementSelected = document.querySelector('.pac-item-selected');
    if (!elementSelected) {
      // Returns the bottom of the list.
      return this.autoCompleteListDecorator(suggestionContainer.lastChild, input);
    } else {
      // Transverse the list in reverse order.
      const previousSibling = elementSelected.previousSibling;
      if (previousSibling) {
       this.autoCompleteListDecorator(previousSibling, input);
      } else {
        // Return to input value
        elementSelected.classList.remove('pac-item-selected');
        input.value = this.autoCompleteInputValue;
      }
    }
  }

  /**
   * Action when key down is trigger
   *
   * @param suggestionContainer
   *   Suggestions container
   * @param input
   *   Input field to listen
   */
  autoCompleteKeyDownInteraction(suggestionContainer, input) {
    let elementSelected = document.querySelector('.pac-item-selected');
    if (!elementSelected) {
      // Start at the top of the list.
      if (suggestionContainer.firstChild) {
        return this.autoCompleteListDecorator(suggestionContainer.firstChild, input);
      }
    } else {
      // Transverse the list from top down.
      const nextSibling = elementSelected.nextSibling;
      if (nextSibling) {
        this.autoCompleteListDecorator(nextSibling, input);
      } else {
        // Return to input value
        elementSelected.classList.remove('pac-item-selected');
        input.value = this.autoCompleteInputValue;
      }
    }
  }

  /**
   * Action when validation is trigger
   *
   * @param suggestionContainer
   *   Suggestions container
   * @param input
   *   Input field to listen
   */
  autoCompleteKeyValidationInteraction(suggestionContainer, input) {
    let elementSelected = document.querySelector('.pac-item-selected');
    if (elementSelected) {
      for (const suggestion of this.autoCompleteSuggestions) {
        let content = elementSelected.textContent || elementSelected.innerText;
        if (content === suggestion.description) {
          this.autoCompleteServiceListener(suggestion, suggestionContainer, input);
        }
      }
      elementSelected.classList.remove('pac-item-selected');
    }
  }

  /**
   * Highlight suggestion selected
   *
   * @param item
   *   Item selected in suggestions container
   * @param input
   *   Input field to listen
   */
  autoCompleteListDecorator(item, input) {
    let elementSelected = document.querySelector('.pac-item-selected');
    if (elementSelected) {
      elementSelected.classList.remove('pac-item-selected');
    }
    input.value = item.textContent || suggestionContainer.innerText;
    item.classList.add('pac-item-selected');
  }

  /**
   * Filter method to return if the suggestion should be displayed
   *
   * @param data
   *   Data to check
   * @returns {Boolean}
   */
  autoCompleteFilterSuggestion(data) {
    try {
      let script = '(function() { var show = true;';
      script += this.component.map.autoCompleteFilter.toString();
      script += '; return show; })()';
      let result = eval(script);
      return result.toString() === 'true';
    }
    catch (e) {
      console.warn('An error occurred in a custom autoComplete filter statement for component ' + this.component.key, e);
      return true;
    }
  }

  /**
   * Clean suggestions list
   *
   * @param suggestionContainer
   *   Container tag
   */
  autoCompleteCleanSuggestions(suggestionContainer) {
    // Clean click listener
    for (const suggestion of this.autoCompleteSuggestions) {
      suggestion.item.removeEventListener('click', suggestion.clickListener);
    }
    this.autoCompleteSuggestions = [];

    // Delete current suggestion list
    while (suggestionContainer.firstChild) {
      suggestionContainer.removeChild(suggestionContainer.firstChild);
    }
  }

  /**
   * Display suggestions when API returns value
   *
   * @param suggestions
   *   Suggestions returned
   * @param status
   *   State returned
   * @param suggestionContainer
   *   Suggestions container
   * @param input
   *   Input field to listen
   */
  autoCompleteDisplaySuggestions(suggestions, status, suggestionContainer, input) {

    // Set the same width as input field
    suggestionContainer.style.width = input.offsetWidth + 'px';

    // Set the default input value
    this.autoCompleteInputValue = input.value;

    this.autoCompleteCleanSuggestions(suggestionContainer);
    if (status != google.maps.places.PlacesServiceStatus.OK) {
      suggestionContainer.style.display = 'none';
      return;
    }

    for (const suggestion of suggestions) {
      if (this.autoCompleteFilterSuggestion(suggestion)) {
        this.autoCompleteSuggestions.push(suggestion);
        this.autoCompleteSuggestionBuilder(suggestion, suggestionContainer, input);
      }
    }

    if (!suggestionContainer.childElementCount) {
      this.autoCompleteCleanSuggestions(suggestionContainer);
      suggestionContainer.style.display = 'none';
    }
    else {
      suggestionContainer.style.display = 'block';
    }
  }

  /**
   * Draw a suggestion in the list
   *
   * @param suggestion
   *   Suggestion to draw
   * @param suggestionContainer
   *   Suggestions container
   * @param input
   *   Input field to listen
   */
  autoCompleteSuggestionBuilder(suggestion, suggestionContainer, input) {

    const item = document.createElement('div');
    item.classList.add('pac-item');

    const itemLogo = document.createElement('span');
    itemLogo.classList.add('pac-icon', 'pac-icon-marker');
    item.appendChild(itemLogo);

    // Draw Main part
    const itemMain = document.createElement('span');
    itemMain.classList.add('pac-item-query');
    if (suggestion.structured_formatting.main_text_matched_substrings) {
      let matches = suggestion.structured_formatting.main_text_matched_substrings;
      for(let k in matches) {
        let part = matches[k];
        if (k == 0 && part.offset > 0) {
          itemMain.appendChild(document.createTextNode(suggestion.structured_formatting.main_text.substring(0, part.offset)));
        }

        const itemBold = document.createElement('span');
        itemBold.classList.add('pac-matched');
        itemBold.appendChild(document.createTextNode(suggestion.structured_formatting.main_text.substring(part.offset, (part.offset + part.length))));
        itemMain.appendChild(itemBold);

        if (k == (matches.length - 1)) {
          let content = suggestion.structured_formatting.main_text.substring((part.offset + part.length));
          if (content.length > 0) {
            itemMain.appendChild(document.createTextNode(content));
          }
        }
      }
    }
    else {
      itemMain.appendChild(document.createTextNode(suggestion.structured_formatting.main_text));
    }
    item.appendChild(itemMain);


    // Draw secondary part
    if (suggestion.structured_formatting.secondary_text) {
      const itemSecondary = document.createElement('span')
      if (suggestion.structured_formatting.secondary_text_matched_substrings) {
        let matches = suggestion.structured_formatting.secondary_text_matched_substrings;
        for(let k in matches) {
          let part = matches[k];
          if (k == 0 && part.offset > 0) {
            itemSecondary.appendChild(document.createTextNode(suggestion.structured_formatting.secondary_text.substring(0, part.offset)));
          }

          const itemBold = document.createElement('span');
          itemBold.classList.add('pac-matched');
          itemBold.appendChild(document.createTextNode(suggestion.structured_formatting.secondary_text.substring(part.offset, (part.offset + part.length))));
          itemSecondary.appendChild(itemBold);

          if (k == (matches.length - 1)) {
            let content = suggestion.structured_formatting.secondary_text.substring((part.offset + part.length));
            if (content.length > 0) {
              itemSecondary.appendChild(document.createTextNode(content));
            }
          }
        }
      }
      else {
        itemSecondary.appendChild(document.createTextNode(suggestion.structured_formatting.secondary_text));
      }
      item.appendChild(itemSecondary);
    }

    suggestionContainer.appendChild(item);

    let clickListener = (event) => {
      input.value = suggestion.description;
      this.autoCompleteInputValue = suggestion.description;
      this.autoCompleteServiceListener(suggestion, suggestionContainer, input);
    };
    suggestion.clickListener = clickListener;
    suggestion.item = item;
    if ('addEventListener' in item){
      item.addEventListener('click', clickListener, false);
    } else if ('attachEvent' in item) {
      item.attachEvent('onclick', clickListener);
    }
  }


  /**
   * Get detailed information and set it as value
   *
   * @param suggestion
   *   Suggestion to draw
   * @param suggestionContainer
   *   Suggestions container
   * @param input
   *   Input field to listen
   */
  autoCompleteServiceListener(suggestion, suggestionContainer, input) {
    const service = new google.maps.places.PlacesService(input);
    service.getDetails({
      placeId: suggestion.place_id,
    }, (place, status) => {
      if (status === google.maps.places.PlacesServiceStatus.OK) {
        this.setValue(place);
      }
    });
  }


  addInput(input, container) {
    super.addInput(input, container);
    BaseComponent.libraryReady('googleMaps').then(() => {
      let autoCompleteOptions = {};
      if (this.component.map) {
        autoCompleteOptions = this.component.map.autoCompleteOptions || {};
        if (autoCompleteOptions.location) {
          autoCompleteOptions.location = new google.maps.LatLng(autoCompleteOptions.location.lat, autoCompleteOptions.location.lng);
        }
      }

      if (this.component.map && this.component.map.autoCompleteFilter) {
        // Call custom autoComplete to filter suggestions
        this.autoCompleteInit(input, autoCompleteOptions);
      }
      else {
        let autocomplete = new google.maps.places.Autocomplete(input);
        autocomplete.addListener("place_changed", () => this.setValue(autocomplete.getPlace()));
      }
    });
  }

  elementInfo() {
    let info = super.elementInfo();
    info.attr.class += ' address-search';
    return info;
  }
}

