        if (mock instanceof Array) {
          _.each(mock, (_mock) => {
            fetchMock.mock(_mock.url, _mock.response, {method: _mock.method});
          });
        }
        else {
          fetchMock.mock(mock.url, mock.response, {method: mock.method});
        }
