'use strict';
import _ from 'lodash';
import _clone from 'lodash/clone';
import _get from 'lodash/get';
import _round from 'lodash/round';
import _pad from 'lodash/pad';
import _chunk from 'lodash/chunk';
import _isNaN from 'lodash/isNaN';
import _has from 'lodash/has';
import _last from 'lodash/last';
import _isBoolean from 'lodash/isBoolean';
import _isString from 'lodash/isString';
import _isNil from 'lodash/isNil';
import _isObject from 'lodash/isObject';
import _isArray from 'lodash/isArray';
import _isPlainObject from 'lodash/isPlainObject';
import _forOwn from 'lodash/forOwn';
import compile from 'lodash/template';
import jsonLogic from 'json-logic-js';

// Configure JsonLogic
jsonLogic.add_operation('_', _);

const FormioUtils = {
  jsonLogic, // Share

  /**
   * Determines the boolean value of a setting.
   *
   * @param value
   * @return {boolean}
   */
  boolValue(value) {
    if (_isBoolean(value)) {
      return value;
    }
    else if (_isString(value)) {
      return (value.toLowerCase() === 'true');
    }
    else {
      return !!value;
    }
  },

  /**
   * Check to see if an ID is a mongoID.
   * @param text
   * @return {Array|{index: number, input: string}|Boolean|*}
   */
  isMongoId(text) {
    return text.toString().match(/^[0-9a-fA-F]{24}$/);
  },

  /**
   * Determine if a component is a layout component or not.
   *
   * @param {Object} component
   *   The component to check.
   *
   * @returns {Boolean}
   *   Whether or not the component is a layout component.
   */
  isLayoutComponent(component) {
    return Boolean(
      (component.columns && Array.isArray(component.columns)) ||
      (component.rows && Array.isArray(component.rows)) ||
      (component.components && Array.isArray(component.components))
    );
  },

  /**
   * Iterate through each component within a form.
   *
   * @param {Object} components
   *   The components to iterate.
   * @param {Function} fn
   *   The iteration function to invoke for each component.
   * @param {Boolean} includeAll
   *   Whether or not to include layout components.
   * @param {String} path
   *   The current data path of the element. Example: data.user.firstName
   * @param {Object} parent
   *   The parent object.
   */
  eachComponent(components, fn, includeAll, path, parent) {
    if (!components) return;
    path = path || '';
    components.forEach((component) => {
      const hasColumns = component.columns && Array.isArray(component.columns);
      const hasRows = component.rows && Array.isArray(component.rows);
      const hasComps = component.components && Array.isArray(component.components);
      let noRecurse = false;
      const newPath = component.key ? (path ? (`${path}.${component.key}`) : component.key) : '';

      // Keep track of parent references.
      if (parent) {
        // Ensure we don't create infinite JSON structures.
        component.parent = _clone(parent);
        delete component.parent.components;
        delete component.parent.componentMap;
        delete component.parent.columns;
        delete component.parent.rows;
      }

      if (includeAll || component.tree || (!hasColumns && !hasRows && !hasComps)) {
        noRecurse = fn(component, newPath);
      }

      const subPath = () => {
        if (
          component.key &&
          (
            [ 'datagrid', 'container', 'editgrid' ].includes(component.type) ||
            component.tree
          )
        ) {
          return newPath;
        }
        else if (
          component.key &&
          component.type === 'form'
        ) {
          return `${newPath}.data`
        }
        return path;
      };

      if (!noRecurse) {
        if (hasColumns) {
          component.columns.forEach((column) =>
            FormioUtils.eachComponent(column.components, fn, includeAll, subPath(), parent ? component : null));
        }

        else if (hasRows) {
          component.rows.forEach((row) => row.forEach((column) =>
            FormioUtils.eachComponent(column.components, fn, includeAll, subPath(), parent ? component : null)));
        }

        else if (hasComps) {
          FormioUtils.eachComponent(component.components, fn, includeAll, subPath(), parent ? component : null);
        }
      }
    });
  },

  /**
   * Matches if a component matches the query.
   *
   * @param component
   * @param query
   * @return {boolean}
   */
  matchComponent(component, query) {
    if (_isString(query)) {
      return component.key === query;
    }
    else {
      let matches = false;
      _forOwn(query, (value, key) => {
        matches = (_get(component, key) === value);
        if (!matches) {
          return false;
        }
      });
      return matches;
    }
  },

  /**
   * Get a component by its key
   *
   * @param {Object} components
   *   The components to iterate.
   * @param {String|Object} key
   *   The key of the component to get, or a query of the component to search.
   *
   * @returns {Object}
   *   The component that matches the given key, or undefined if not found.
   */
  getComponent(components, key) {
    let result;
    FormioUtils.eachComponent(components, (component, path) => {
      if (FormioUtils.matchComponent(component, key)) {
        component.path = path;
        result = component;
        return true;
      }
    });
    return result;
  },

  /**
   * Finds a component provided a query of properties of that component.
   *
   * @param components
   * @param query
   * @return {*}
   */
  findComponents(components, query) {
    let results = [];
    FormioUtils.eachComponent(components, (component, path) => {
      if (FormioUtils.matchComponent(component, query)) {
        component.path = path;
        results.push(component);
      }
    }, true);
    return results;
  },

  /**
   * Flatten the form components for data manipulation.
   *
   * @param {Object} components
   *   The components to iterate.
   * @param {Boolean} includeAll
   *   Whether or not to include layout components.
   *
   * @returns {Object}
   *   The flattened components map.
   */
  flattenComponents(components, includeAll) {
    let flattened = {};
    FormioUtils.eachComponent(components, (component, path) => {
      flattened[path] = component;
    }, includeAll);
    return flattened;
  },

  /**
   * Returns if this component has a conditional statement.
   *
   * @param component - The component JSON schema.
   *
   * @returns {boolean} - TRUE - This component has a conditional, FALSE - No conditional provided.
   */
  hasCondition(component) {
    return Boolean(
      (component.customConditional) ||
      (component.conditional && component.conditional.when) ||
      (component.conditional && component.conditional.json)
    );
  },

  /**
   * Extension of standard #parseFloat(value) function, that also clears input string.
   *
   * @param {any} value
   *   The value to parse.
   *
   * @returns {Number}
   *   Parsed value.
   */
  parseFloat(value) {
    return parseFloat(_isString(value)
      ? value.replace(/[^\de.+-]/gi, '')
      : value);
  },

  /**
   * Formats provided value in way how Currency component uses it.
   *
   * @param {any} value
   *   The value to format.
   *
   * @returns {String}
   *   Value formatted for Currency component.
   */
  formatAsCurrency(value) {
    const parsedValue = this.parseFloat(value);

    if (_isNaN(parsedValue)) {
      return '';
    }

    const parts = _round(parsedValue, 2)
      .toString()
      .split('.');
    parts[0] = _chunk(Array.from(parts[0]).reverse(), 3)
      .reverse()
      .map((part) => part
        .reverse()
        .join(''))
      .join(',');
    parts[1] = _pad(parts[1], 2, '0');
    return parts.join('.');
  },

  /**
   * Escapes RegEx characters in provided String value.
   *
   * @param {String} value
   *   String for escaping RegEx characters.
   * @returns {string}
   *   String with escaped RegEx characters.
   */
  escapeRegExCharacters(value) {
    return value.replace(/[\-\[\]\/\{\}\(\)\*\+\?\.\\\^\$\|]/g, "\\$&");
  },

  /**
   * Checks the calculated value for a provided component and data.
   *
   * @param {Object} component
   *   The component to check for the calculated value.
   * @param {Object} submission
   *   A submission object.
   * @param data
   *   The full submission data.
   */
  checkCalculated(component, submission, data) {
    // Process calculated value stuff if present.
    if (component.calculateValue) {
      if (_isString(component.calculateValue)) {
        try {
          const util = this;
          data[component.key] = eval(`(function(data, util) { var value = [];${component.calculateValue.toString()}; return value; })(data, util)`);
        }
        catch (e) {
          console.warn(`An error occurred calculating a value for ${component.key}`, e);
        }
      }
      else {
        try {
          data[component.key] = this.jsonLogic.apply(component.calculateValue, {
            data: submission ? submission.data : data,
            row: data
          });
        }
        catch (e) {
          console.warn(`An error occurred calculating a value for ${component.key}`, e);
        }
      }
    }
  },

  /**
   * Checks the conditions for a provided component and data.
   *
   * @param component
   *   The component to check for the condition.
   * @param row
   *   The data within a row
   * @param data
   *   The full submission data.
   *
   * @returns {boolean}
   */
  checkCondition(component, row, data) {
    if (component.customConditional) {
      try {
        const script = `(function() { var show = true;${component.customConditional.toString()}; return show; })()`;
        const result = eval(script);
        return result.toString() === 'true';
      }
      catch (e) {
        console.warn(`An error occurred in a custom conditional statement for component ${component.key}`, e);
        return true;
      }
    }
    else if (component.conditional && component.conditional.when) {
      const cond = component.conditional;
      let value = null;
      if (row) {
        value = this.getValue({data: row}, cond.when);
      }
      if (data && _isNil(value)) {
        value = this.getValue({data: data}, cond.when);
      }
      // FOR-400 - Fix issue where falsey values were being evaluated as show=true
      if (_isNil(value)) {
        return false;
      }
      // Special check for selectboxes component.
      if (_isObject(value) && _has(value, cond.eq)) {
        return value[cond.eq].toString() === cond.show.toString();
      }
      // FOR-179 - Check for multiple values.
      if (_isArray(value) && value.includes(cond.eq)) {
        return (cond.show.toString() === 'true');
      }

      return (value.toString() === cond.eq.toString()) === (cond.show.toString() === 'true');
    }
    else if (component.conditional && component.conditional.json) {
      return jsonLogic.apply(component.conditional.json, {
        data,
        row
      });
    }

    // Default to show.
    return true;
  },

  /**
   * Get the value for a component key, in the given submission.
   *
   * @param {Object} submission
   *   A submission object to search.
   * @param {String} key
   *   A for components API key to search for.
   */
  getValue(submission, key) {
    const search = (data) => {
      if (_isPlainObject(data)) {
        if (_has(data, key)) {
          return data[key];
        }

        let value = null;

        _forOwn(data, (prop) => {
          const result = search(prop);
          if (!_isNil(result)) {
            value = result;
            return false;
          }
        });

        return value;
      }
      else {
        return null
      }
    };

    return search(submission.data);
  },

  /**
   * Interpolate a string and add data replacements.
   *
   * @param string
   * @param data
   * @returns {XML|string|*|void}
   */
  interpolate(string, data) {
    const templateSettings = {
      evaluate: /\{\%(.+?)\%\}/g,
      interpolate: /\{\{(.+?)\}\}/g,
      escape: /\{\{\{(.+?)\}\}\}/g
    };
    try {
      return compile(string, templateSettings)(data);
    }
    catch (err) {
      console.warn('Error interpolating template', err, string, data);
    }
  },

  /**
   * Make a filename guaranteed to be unique.
   * @param name
   * @returns {string}
   */
  uniqueName(name) {
    const parts = name.toLowerCase().replace(/[^0-9a-z\.]/g, '').split('.');
    const fileName = parts[0];
    const ext = parts.length > 1
      ? `.${_last(parts)}`
      : '';
    return `${fileName.substr(0, 10)}-${this.guid()}${ext}`;
  },

  guid() {
    return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, (c) => {
      const r = Math.random()*16|0;
      const v = c === 'x'
        ? r
        : (r&0x3|0x8);
      return v.toString(16);
    });
  }
};

module.exports = global.FormioUtils = FormioUtils;

'use strict';

/* eslint-env mocha */
const expect = require('chai').expect;
const writtenNumber = require('written-number');
const utils = require('./index');
const components = require('./fixtures/components.json');
const submission1 = require('./fixtures/submission1.json');

describe('eachComponent', () => {
  it('should iterate through nested components in the right order', () => {
    let n = 1;
    utils.eachComponent(components, (component) => {
      expect(component.order).to.equal(n);
      n += 1;
    });
  });

  it('should include layouts components if provided', () => {
    let numComps = 0;
    let numLayout = 0;
    utils.eachComponent(components, (component) => {
      if (utils.isLayoutComponent(component)) {
        numLayout++;
      }
      else {
        numComps++;
      }
    }, true);
    expect(numLayout).to.be.equal(3);
    expect(numComps).to.be.equal(8);
  });

  it('Should provide the paths to all of the components', () => {
    const paths = [
      'one',
      'parent1',
      'two',
      'parent2',
      'three',
      '',
      'four',
      'five',
      'six',
      'seven',
      'eight'
    ];
    const testPaths = [];
    utils.eachComponent(components, (component, path) => {
      testPaths.push(path);
    }, true);
    expect(paths).to.deep.equal(testPaths);
  });

  it('Should be able to find all textfield components', () => {
    const comps = utils.findComponents(components, {type: 'textfield'});
    expect(comps.length).to.equal(6);
  });

  it('Should be able to find components with special properties.', () => {
    const components3 = require('./fixtures/components3.json');
    const comps = utils.findComponents(components3, {'properties.path': 'a'});
    expect(comps.length).to.equal(4);
    expect(comps[0].key).to.equal('b');
    expect(comps[1].key).to.equal('e');
    expect(comps[2].key).to.equal('j');
    expect(comps[3].key).to.equal('m');
  });

  it('Should be able to generate paths based on component types', () => {
    const components = require('./fixtures/components2.json');
    const paths = [
      'a',
      'b',
      'c',
      'd',
      'f',
      'f.g',
      'f.h',
      'f.i',
      'e',
      'j',
      'k',
      'k.n',
      'k.n.o',
      'k.n.p',
      'k.n.q',
      'k.m',
      'k.l',
      'r',
      'submit'
    ];
    const testPaths = [];
    utils.eachComponent(components, (component, path) => {
      testPaths.push(path);
    }, true);
    expect(paths).to.deep.equal(testPaths);
  });

  it('Should still provide the correct paths when it is not recursive', () => {
    const paths = [
      'a',
      'd',
      'f',
      'f.g',
      'f.h',
      'f.i',
      'e',
      'j',
      'k',
      'k.n',
      'k.n.o',
      'k.n.p',
      'k.n.q',
      'k.m',
      'k.l',
      'r',
      'submit'
    ];
    const testPaths = [];
    utils.eachComponent(require('./fixtures/components2.json'), (component, path) => {
      testPaths.push(path);
    });
    expect(paths).to.deep.equal(testPaths);
  });

  it('should be able to block recursion', () => {
    let numComps = 0;
    let numLayout = 0;
    utils.eachComponent(components, (component) => {
      if (utils.isLayoutComponent(component)) {
        numLayout++;
      }
      else {
        numComps++;
      }

      if (component.type === 'table') {
        let numInTable = 0;
        [].concat.apply([], component.rows).forEach((row) => {
          utils.eachComponent(row.components, () => {
            numInTable++;
          });
        });
        expect(numInTable).to.be.equal(4);
        return true;
      }
    }, true);
    expect(numLayout).to.be.equal(3);
    expect(numComps).to.be.equal(4);
  });
});

describe('getComponent', () => {
  it('should return the correct components', () => {
    for (let n = 1; n <= 8; n += 1) {
      const component = utils.getComponent(components, writtenNumber(n));
      expect(component).not.to.be.null;
      expect(component).not.to.be.undefined;
      expect(component).to.be.an('object');
      expect(component.order).to.equal(n);
      expect(component.key).to.equal(writtenNumber(n));
    }
  });

  it('should work with a different this context', () => {
    for (let n = 1; n <= 8; n += 1) {
      const component = utils.getComponent.call({}, components, writtenNumber(n));
      expect(component).not.to.be.null;
      expect(component).not.to.be.undefined;
      expect(component).to.be.an('object');
      expect(component.order).to.equal(n);
      expect(component.key).to.equal(writtenNumber(n));
    }
  });
});

describe('flattenComponents', () => {
  it('should return an object of flattened components', () => {
    const flattened = utils.flattenComponents(components);
    for (let n = 1; n <= 8; n += 1) {
      const component = flattened[writtenNumber(n)];
      expect(component).not.to.be.undefined;
      expect(component).to.be.an('object');
      expect(component.order).to.equal(n);
      expect(component.key).to.equal(writtenNumber(n));
    }
  });

  it('should work with a different this context', () => {
    const flattened = utils.flattenComponents.call({}, components);
    for (let n = 1; n <= 8; n += 1) {
      const component = flattened[writtenNumber(n)];
      expect(component).not.to.be.undefined;
      expect(component).to.be.an('object');
      expect(component.order).to.equal(n);
      expect(component.key).to.equal(writtenNumber(n));
    }
  });
});

describe('getValue', () => {
  it('should be able to get a simple value', () => {
    expect(utils.getValue(submission1, 'name')).to.be.equal(submission1.data.name);
  });

  it('should be able to get a value from a container', () => {
    expect(utils.getValue(submission1, 'animalname')).to.be.equal(submission1.data.mycontainer.animalname);
  });
});

describe('parseFloat', () => {
  it('should clear input and parse value', () => {
    expect(utils.parseFloat('12,345,678.90')).to.be.equal(12345678.90);
  });
});

describe('formatAsCurrency', () => {
  it('should be able to format Float value for Currency component', () => {
    expect(utils.formatAsCurrency(123.4)).to.be.equal('123.40');
    expect(utils.formatAsCurrency(12345678.9)).to.be.equal('12,345,678.90');
    expect(utils.formatAsCurrency(12345678.915)).to.be.equal('12,345,678.92');
  });

  it('should be able to format String value for Currency component', () => {
    expect(utils.formatAsCurrency('12345678.915')).to.be.equal('12,345,678.92');
  });
});

describe('checkCalculated', () => {
  it('should be able to calculate value based on javascript code', () => {
    const component = {
      key: 'sum',
      calculateValue: 'value = 3'
    };
    const data = {};

    utils.checkCalculated(component, null, data);
    expect(data.sum).to.be.equal(3);
  });

  it('should be able to calculate value based on json logic', () => {
    const component = {
      key: 'sum',
      calculateValue: {
        '_.sum': { var: 'data.test' }
      }
    };
    const data = { test: [ 1, 2, 3 ] };

    utils.checkCalculated(component, null, data);
    expect(data.sum).to.be.equal(6);
  });
});

describe('checkCondition', () => {
  it('should display component by default', () => {
    expect(utils.checkCondition({}, null, {})).to.be.equal(true);
  });

  it('should be able to calculate condition based on javascript code', () => {
    const component = {
      key: 'sum',
      customConditional: 'show = data.test === 3'
    };
    const data1 = { test: 3 };
    const data2 = { test: 5 };

    expect(utils.checkCondition(component, null, data1)).to.be.equal(true);
    expect(utils.checkCondition(component, null, data2)).to.be.equal(false);
  });

  it('should be able to calculate condition based on json logic', () => {
    const component = {
      key: 'sum',
      conditional: {
        json: {
          '===': [
            { '_.sum': { var: 'data.test' } },
            6
          ]
        }
      }
    };
    const data1 = { test: [ 1, 2, 3 ] };
    const data2 = { test: [ 1, 2, 4 ] };

    expect(utils.checkCondition(component, null, data1)).to.be.equal(true);
    expect(utils.checkCondition(component, null, data2)).to.be.equal(false);
  });
});
