];

export default {
  'input': true,
  'tableView': true,
  'label': 'address',
  'key': 'address',
  'placeholder': '',
  'multiple': false,
  'protected': false,
  'clearOnHide': true,
  'unique': false,
  'persistent': true,
  'map': {
    'region': '',
    'key': '',
    'autocompleteOptions': null,
    'autocompleteFilter': null
  },
  'validate': {
    'customPrivate': false,
    'custom': '',
    'required': false
