  buildRows(values) {
    if (!this.tbody) {
      return;
    }
    const allowReorder = this.allowReorder;
    this.inputs = [];
    this.tbody.innerHTML = '';
    values = values || this.dataValue;
    _.each(values, (value, index) => {
      const tr = this.ce('tr');
      if (allowReorder) {
        tr.appendChild(this.ce('td', {
          class: 'formio-drag-column'
        }, this.dragButton()));
      }
      const td = this.ce('td');
      this.buildInput(td, value, index);
