        const values = value.split('/').map(x => parseInt(x, 10)),
          day = values[DAY],
          month = values[MONTH],
          year = values[YEAR],
          maxDay = getDaysInMonthCount(month, year);

        if (day < 0 || day > maxDay) {
          return false;
        }
        if (month < 0 || month > 12) {
          return false;
