import Promise from 'native-promise-only';

import _ from 'lodash';

import Formio from './Formio';
import Webform from './Webform';

export default class PDF extends Webform {
  constructor(element, options) {
    super(element, options);

    // Resolve when the iframe is ready.
    this.iframeReady = new Promise((resolve) => (this.iframeReadyResolve = resolve));
  }

  postMessage(message) {
    if (!message.type) {
      message.type = 'iframe-data';
    }
    this.iframeReady.then(() => {
      if (this.iframe && this.iframe.contentWindow) {
        this.iframe.contentWindow.postMessage(JSON.stringify(message), '*');
      }
    });
  }

  // Do not clear the iframe.
  clear() {}
  redraw() {
    this.postMessage({ name: 'redraw' });
  }

  getSrc() {
    if (!this._form || !this._form.settings || !this._form.settings.pdf) {
      return '';
    }
    let iframeSrc = `${this._form.settings.pdf.src}.html`;
    const params = [`id=${this.id}`];
    if (this.options.readOnly) {
      params.push('readonly=1');
    }
    if (this.options.zoom) {
      params.push(`zoom=${this.options.zoom}`);
    }
    if (this.options.builder) {
      params.push('builder=1');
    }
    if (params.length) {
      iframeSrc += `?${params.join('&')}`;
    }
    return iframeSrc;
  }

  setForm(form) {
    return super.setForm(form).then(() => {
      if (this.formio) {
        form.projectUrl = this.formio.projectUrl;
        form.url = this.formio.formUrl;
        form.base = this.formio.base;
        this.postMessage({ name: 'token', data: this.formio.getToken() });
      }
      this.postMessage({ name: 'form', data: form });
    });
  }

  setSubmission(submission) {
    submission.readOnly = !!this.options.readOnly;
    this.postMessage({ name: 'submission', data: submission });
    return super.setSubmission(submission).then(() => {
      if (this.formio) {
        this.formio.getDownloadUrl().then((url) => {
          // Add a download button if it has a download url.
          if (!url) {
            return;
          }
          if (!this.downloadButton) {
            if (this.options.primaryProject) {
              url += `&project=${this.options.primaryProject}`;
            }
            this.downloadButton = this.ce('a', {
              href: url,
              target: '_blank',
              style: 'position:absolute;right:10px;top:110px;cursor:pointer;'
            }, this.ce('img', {
              src: require('./pdf.image'),
              style: 'width:3em;'
            }));
            this.element.insertBefore(this.downloadButton, this.iframe);
          }
        });
      }
    });
  }

  addComponent(component, element, data, before, noAdd, state) {
    // Never add the component to the DOM.
    super.addComponent(component, element, data, before, true, state);
  }

  // Iframe should always be shown.
  showElement() {}
  build() {
    // Do not rebuild the iframe...
    if (this.iframe) {
      this.addComponents();
      return;
    }

    this.zoomIn = this.ce('span', {
      style: 'position:absolute;right:10px;top:10px;cursor:pointer;',
      class: 'btn btn-default btn-secondary no-disable'
    }, this.ce('i', {
      class: this.iconClass('zoom-in')
    }));
    this.addEventListener(this.zoomIn, 'click', (event) => {
      event.preventDefault();
      this.postMessage({ name: 'zoomIn' });
    });

    this.zoomOut = this.ce('span', {
      style: 'position:absolute;right:10px;top:60px;cursor:pointer;',
      class: 'btn btn-default btn-secondary no-disable'
    }, this.ce('i', {
      class: this.iconClass('zoom-out')
    }));
    this.addEventListener(this.zoomOut, 'click', (event) => {
      event.preventDefault();
      this.postMessage({ name: 'zoomOut' });
    });

    this.iframe = this.ce('iframe', {
      src: this.getSrc(),
      id: `iframe-${this.id}`,
      seamless: true,
      class: 'formio-iframe'
    });

    // Handle an iframe submission.
    this.on('iframe-submission', (submission) => {
      this.setSubmission(submission).then(() => this.submit());
    }, true);

    // Trigger when this form is ready.
    this.on('iframe-ready', () => this.iframeReadyResolve(), true);

    this.appendChild(this.element, [
      this.zoomIn,
      this.zoomOut,
      this.iframe
    ]);

    if (
      !this.options.readOnly &&
      _.find(this.form.components, (component) => component.type === 'button' && component.action === 'submit')
    ) {
      this.submitButton = this.ce('button', {
        type: 'button',
        class: 'btn btn-primary'
      }, 'Submit');

      this.addEventListener(this.submitButton, 'click', () => {
        this.postMessage({ name: 'getSubmission' });
      });
      this.appendChild(this.element, this.submitButton);
    }

    this.addComponents();
  }
}

/**
 * Listen for window messages.
 */
window.addEventListener('message', (event) => {
  let eventData = null;
  try {
    eventData = JSON.parse(event.data);
  }
  catch (err) {
    eventData = null;
  }

  // If this form exists, then emit the event within this form.
  if (
    eventData &&
    eventData.name &&
    eventData.formId &&
    Formio.forms.hasOwnProperty(eventData.formId)
  ) {
    Formio.forms[eventData.formId].emit(`iframe-${eventData.name}`, eventData.data);
  }
});

import _ from 'lodash';

import WebformBuilder from './WebformBuilder';
import { getElementRect } from './utils/utils';
import PDF from './PDF';

export default class PDFBuilder extends WebformBuilder {
  get defaultComponents() {
    return {
      pdf: {
        title: 'PDF Fields',
        weight: 0,
        default: true,
        components: {
          textfield: true,
          number: true,
          password: true,
          email: true,
          phoneNumber: true,
          currency: true,
          checkbox: true,
          signature: true,
          select: true,
          textarea: true,
          datetime: true
        }
      },
      basic: false,
      advanced: false,
      layout: false,
      data: false,
      premium: false,
      resource: false
    };
  }

  addDropZone() {
    if (!this.dropZone) {
      this.dropZone = this.ce('div', {
        class: 'formio-drop-zone'
      });
      this.prepend(this.dropZone);
    }
    this.addEventListener(this.dropZone, 'dragover', (event) => {
      event.preventDefault();
      return false;
    });
    this.addEventListener(this.dropZone, 'drop', (event) => {
      event.preventDefault();
      this.dragStop(event);
      return false;
    });
    this.disableDropZone();
  }

  render() {
    return this.onElement.then(() => {
      this.build(this.clear());
      this.isBuilt = true;
      this.on('resetForm', () => this.resetValue(), true);
      this.on('refreshData', () => this.updateValue(), true);
      setTimeout(() => {
        this.onChange();
        this.emit('render');
      }, 1);
    });
  }

  enableDropZone() {
    if (this.dropZone) {
      const iframeRect = getElementRect(this.pdfForm.element);
      this.dropZone.style.height = iframeRect && iframeRect.height  ? `${iframeRect.height}px` : '1000px';
      this.dropZone.style.width = iframeRect && iframeRect.width ? `${iframeRect.width}px` : '100%';
      this.addClass(this.dropZone, 'enabled');
    }
  }

  disableDropZone() {
    if (this.dropZone) {
      this.removeClass(this.dropZone, 'enabled');
    }
  }

  addComponentTo(parent, schema, element, sibling) {
    const comp = super.addComponentTo(parent, schema, element, sibling);
    comp.isNew = true;
    if (this.pdfForm && schema.overlay) {
      this.pdfForm.postMessage({ name: 'addElement', data: schema });
    }
    return comp;
  }

  addComponent(component, element, data, before, noAdd, state) {
    return super.addComponent(component, element, data, before, true, state);
  }

  deleteComponent(component) {
    if (this.pdfForm && component.component) {
      this.pdfForm.postMessage({ name: 'removeElement', data: component.component });
    }
    return super.deleteComponent(component);
  }

  dragStart(event, component) {
    event.stopPropagation();
    event.dataTransfer.setData('text/plain', 'true');
    this.currentComponent = component;
    this.enableDropZone();
  }

  removeEventListeners(all) {
    super.removeEventListeners(all);
    _.each(this.groups, (group) => {
      _.each(group.components, (builderComponent) => {
        this.removeEventListener(builderComponent, 'dragstart');
        this.removeEventListener(builderComponent, 'dragend');
      });
    });
  }

  clear() {
    const state = {};
    this.destroy(state);
    return state;
  }
  redraw() {
    if (this.pdfForm) {
      this.pdfForm.postMessage({ name: 'redraw' });
    }
  }

  dragStop(event) {
    const schema = this.currentComponent ? this.currentComponent.schema : null;
    if (!schema) {
      return false;
    }

    schema.overlay = {
      top: event.offsetY,
      left: event.offsetX,
      width: 100,
      height: 20
    };

    this.addComponentTo(this, schema, this.getContainer());
    this.disableDropZone();
    return false;
  }

  // Don't need to add a submit button here... the pdfForm will already do this.
  addSubmitButton() {}

  addBuilderComponent(component, group) {
    const builderComponent = super.addBuilderComponent(component, group);
    if (builderComponent) {
      builderComponent.element.draggable = true;
      builderComponent.element.setAttribute('draggable', true);
      this.addEventListener(builderComponent.element, 'dragstart', (event) => this.dragStart(event, component), true);
      this.addEventListener(builderComponent.element, 'dragend', () => this.disableDropZone(), true);
    }
    return builderComponent;
  }

  refreshDraggable() {
    this.addSubmitButton();
    this.builderReadyResolve();
  }

  build() {
    this.buildSidebar();
    if (!this.pdfForm) {
      this.element.noDrop = true;
      this.pdfForm = new PDF(this.element, this.options);
      this.addClass(this.pdfForm.element, 'formio-pdf-builder');
    }
    this.pdfForm.destroy();
    this.pdfForm.on('iframe-elementUpdate', schema => {
      const component = this.getComponentById(schema.id);
      if (component && component.component) {
        component.component.overlay = {
          page: schema.page,
          left: schema.left,
          top: schema.top,
          height: schema.height,
          width: schema.width
        };
        this.editComponent(component);
        this.emit('updateComponent', component);
      }
      return component;
    }, true);
    this.pdfForm.on('iframe-componentUpdate', schema => {
      const component = this.getComponentById(schema.id);
      if (component && component.component) {
        component.component.overlay = {
          page: schema.overlay.page,
          left: schema.overlay.left,
          top: schema.overlay.top,
          height: schema.overlay.height,
          width: schema.overlay.width
        };
        this.emit('updateComponent', component);
        this.emit('change', this.form);
      }
      return component;
    }, true);
    this.pdfForm.on('iframe-componentClick', schema => {
      const component = this.getComponentById(schema.id);
      if (component) {
        this.editComponent(component);
      }
    }, true);
    this.addComponents();
    this.addDropZone();
    this.updateDraggable();
    this.formReadyResolve();
  }

  setForm(form) {
    return super.setForm(form).then(() => {
      return this.ready.then(() => {
        if (this.pdfForm) {
          this.pdfForm.postMessage({ name: 'form', data: form });
          return this.pdfForm.setForm(form);
        }
        return form;
      });
    });
  }
}

